import React from 'react';
import './App.css';
import Course from './Course';

class CourseArea extends React.Component {
  getCourses() {
    let courses = [];

    if (Array.isArray(this.props.data)){
     for(let i =0; i < this.props.data.length; i++){
      courses.push (
        <Course key={i} complete ={this.props.complete} data={this.props.data[i]} 
        courseKey={this.props.data[i].number} addCartCourse={(data) => this.props.addCartCourse(data)} 
        removeCartCourse={(data) => this.props.removeCartCourse(data)} cartCourses={this.props.cartCourses}
        allCourses = {this.props.all}/>
      )
    }
  }
  else{
    for(const course of Object.keys(this.props.data)){
      courses.push (
        <Course key={this.props.data[course].number} complete ={this.props.complete} 
        data={this.props.data[course]} courseKey={this.props.data[course].number} 
        addCartCourse={(data) => this.props.addCartCourse(data)} 
        removeCartCourse={(data) => this.props.removeCartCourse(data)} cartCourses={this.props.cartCourses}
        allCourses = {this.props.all}/>
      )
    }
  }

    return courses;
  }

  shouldComponentUpdate(nextProps) {
    return (JSON.stringify(this.props) !== JSON.stringify(nextProps))
  }

  render() {
    return (
      <div style={{margin: 5, marginTop: -5}}>
        {this.getCourses()}
      </div>
    )
  }
}

export default CourseArea;
